targets.expr:
# net income
(
  sum(
    sum(
      sum_over_time(hledger_transactions{account=~"revenue:.*",}[30d])
    ) by (currency)
    * on(currency) last_over_time(hledger_fx_rate{target_currency="$target_currency"}[1d])
  ) by(target_currency) * -1
  - on (target_currency)
  sum(
    sum(
      sum_over_time(hledger_transactions{account="expenses:Taxes:Income"}[30d])
    ) by (currency)
    * on(currency) last_over_time(hledger_fx_rate{target_currency="$target_currency"}[1d])
  ) by(target_currency)
)

# expenses
(
  sum(
    sum(
      sum_over_time(hledger_transactions{account=~"expenses:.*",account!="expenses:Taxes:Income"}[30d])
    ) by (currency)
    * on(currency) last_over_time(hledger_fx_rate{target_currency="$target_currency"}[1d])
  ) by(target_currency)
)

sum(
    sum(
        last_over_time(hledger_balance{account=~"assets:investments:warrants.*"}[1d])
    ) by(currency) 
    * on(currency) last_over_time(hledger_fx_rate{target_currency="$target_currency"}[1d])
) or vector(0)

sum(
    sum(
        last_over_time(hledger_balance{account=~"assets:investments:warrants.*"}[1d])
    ) by(currency) 
    * on(currency) last_over_time(hledger_fx_rate{target_currency="$target_currency"}[1d])
) or vector(0)

sum(
    sum(
        last_over_time(hledger_transactions{
            account=~"revenue:Salary:.*"}[33d])
    ) by(currency) 
    * on(currency) last_over_time(hledger_fx_rate{target_currency="$target_currency"}[1d])
)

* 12 * 4.5 * -1

(
  sum(
    sum(
      last_over_time(hledger_transactions{account=~"expenses:.*",account!="expenses:Taxes:Income"}[1d])
    ) by (currency)
    * on(currency) last_over_time(hledger_fx_rate{target_currency="$target_currency"}[1d])
  )

  + ignoring(year, month) group_right
    count_values without() ("year", year(timestamp(
      count_values without() ("month", month(timestamp(
        sum(
          sum(
            last_over_time(hledger_transactions{account=~"expenses:.*",account!="expenses:Taxes:Income"}[1d])
          ) by (currency)
          * on(currency) last_over_time(hledger_fx_rate{target_currency="$target_currency"}[1d])
        )
      )))
    ))) * 0
)
